<?phprequire('MainController.php');//include_once('../Helpers/PHPMailer_5.2.4/class.phpmailer.php');class LoginController extends MainController{    public function loadMetaValue($meta_key){        $api = new Login;        $info = $api->loadMetaValue($meta_key);        if(count($info)>0){            $info = $info['meta_value'];        }else{            $info = "";        }        return $info;            }        public  function isValidLogin($user_name,$password){                $api = new Login;        $info = $api->isValidLogin($user_name,$password);        return $info;            }    public function createAuthCode($lenth=9){				$chars = "ABCDEFGHIJKLMNOPQRSTUVEWXYZabcdefghijkmnopqrstuvwxyz023456789";    	    srand((double)microtime()*1000000);     	$i = 0;     	$pass = '' ;     	while ($i <= $lenth) {         	$num = rand() % 33;         	$tmp = substr($chars, $num, 1);         	$pass = $pass . $tmp;         	$i++;    	  }    		 return $pass;				}    public  function updateSid($sid,$user_id){                $api = new Login;        $info = $api->updateSid($sid,$user_id);        return $info;            }    }